DECLARE
  v_count NUMBER;
BEGIN
  SELECT COUNT(*) INTO v_count FROM employees WHERE salary > 50000;
  DBMS_OUTPUT.PUT_LINE('High earners count: ' || v_count);
END;

{
  "parameters": [
    {"name": "p_emp_id", "type": "NUMBER", "direction": "IN"},
    {"name": "p_salary_increase", "type": "NUMBER", "direction": "IN"},
    {"name": "p_result", "type": "VARCHAR2", "direction": "OUT"}
  ],
  "procedureBody": "BEGIN
  UPDATE employees SET salary = salary * (1 + p_salary_increase/100) WHERE employee_id = p_emp_id;
  p_result := 'Success';
  COMMIT;
EXCEPTION
  WHEN OTHERS THEN
    p_result := 'Error: ' || SQLERRM;
    ROLLBACK;
END;"
}

